InlineDCT := DICTIONARY([{0 => 'Black' , 'Greys'},
                         {1 => 'Brown' , 'Earth'},
                         {2 => 'Red'   , 'Reds'},
                         {3 => 'White' , 'Greys'} ], 
                        {UNSIGNED code => STRING10 color ,STRING10 Tone}) : ONCE;

UNSIGNED v := 0 : STORED('v');

OUTPUT(InlineDCT[v].color);
